var ascii = msg.params;
// If msg.params is already a string with quotes, remove them:
if (typeof ascii === 'string' && ascii.startsWith('"') && ascii.endsWith('"')) {
    ascii = ascii.substring(1, ascii.length - 1);
}
var result = {
  contentType: "TEXT",   // Very important: ensures it's sent as raw text, not JSON
  data: ascii,           // Just the plain string
  metadata: {
    topic: "write"       // MQTT topic to publish to
  }
};

return result;
